function table4_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('table4');
	if(!$arrPerm['insert']) return false;

	$data = [
		'field2' => Request::val('field2', ''),
		'field3' => Request::lookup('field3', ''),
		'field4' => Request::lookup('field4', ''),
		'field19' => Request::lookup('field19', ''),
	];


	// hook: table4_before_insert
	if(function_exists('table4_before_insert')) {
		$args = [];
		if(!table4_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('table4', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('table4', $recID, calculated_fields()['table4']);

	// hook: table4_after_insert
	if(function_exists('table4_after_insert')) {
		$res = sql("SELECT * FROM `table4` WHERE `ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!table4_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('table4', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) table4_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

