function table4_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('table4', $selected_id, 'edit')) return false;

	$data = [
		'field2' => Request::val('field2', ''),
		'field3' => Request::lookup('field3', ''),
		'field4' => Request::lookup('field4', ''),
		'field19' => Request::lookup('field19', ''),
	];

	// get existing values
	$old_data = getRecord('table4', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: table4_before_update
	if(function_exists('table4_before_update')) {
		$args = ['old_data' => $old_data];
		if(!table4_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'table4', 
		backtick_keys_once($set), 
		['`ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="table4_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('table4', $data['selectedID'], calculated_fields()['table4']);

	// hook: table4_after_update
	if(function_exists('table4_after_update')) {
		$res = sql("SELECT * FROM `table4` WHERE `ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['ID'];
		$args = ['old_data' => $old_data];
		if(!table4_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='table4' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

