		$rdata = $row;
	}
	$templateCode .= loadView('table4-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: table4_dv
	if(function_exists('table4_dv')) {
		$args=[];
		table4_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}